import React from 'react';
import PropTypes from 'prop-types';

class Algorithm extends React.Component {

    state = {
        picked: ''
    }

    algorithmStyle = () => {
        this.state.picked = this.props.algorithm.name
        return {
            backgroundColor: this.state.picked === this.props.algorithm.name ?
                '#f4f4f4' : '#ff0000',
            padding: '10px',
            borderBottom: '2px #ccc dotted'
        }
    }

    pickedAlgorithm = () => {

    }

    render() {
        return (
            <div style={this.algorithmStyle()}>
                <p>
                    <input type="radio" value={this.props.algorithm.name} name="algorithm" onChange={this.pickedAlgorithm} />
                    {this.props.algorithm.name} 
                </p>
            </div>
        );
    }
}

Algorithm.propTypes = {
    algorithm: PropTypes.object.isRequired
}

export default Algorithm;
